package GroupProject;

public class code7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] a = {
		        {1,1,2},
		        {3,1,2},
		        {3,5,3},
		        {0,1,2}
		    };
		    int sum=0;
		    for (int i=0; i<a.length;i++){
		      for (int j=0;j<a[i].length;j++){
		        sum+=a[i][j];
		      }
		      //System.out.println(sum);
		      //  sum=0;
		       
		     
		       
		    }
		    System.out.println(sum);
		    sum=0;

	}

}
